siEx cScrBuf::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cScrBuf* pthis1 = (cScrBuf*) pthis;

      switch(index) {
         case 0:
            if (argc > 3)
               return o3_new(cEx)("Invalid argument count. ( toString )");
            *rval = pthis1->toString(argc > 0 ? argv[0].toStr() : "utf8",argc > 1 ? argv[1].toInt32() : 0,argc > 2 ? argv[2].toInt32() : -1);
            break;
         case 1:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( length )");
            *rval = pthis1->length();
            break;
         case 2:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( setLength )");
            *rval = pthis1->setLength(argv[0].toInt32());
            break;
         case 3:
            if (argc < 1 && argc > 2)
               return o3_new(cEx)("Invalid argument count. ( byteLength )");
            *rval = pthis1->byteLength(argv[0].toStr(),argc > 1 ? argv[1].toStr() : "utf8");
            break;
         case 4:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( append )");
            pthis1->append(Buf(siBuf(argv[0].toScr())));
            break;
         case 5:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( __query__ )");
            *rval = pthis1->__query__(argv[0].toInt32());
            break;
         case 6:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( __getter__ )");
            *rval = pthis1->__getter__(argv[0].toInt32());
            break;
         case 7:
            if (argc != 2)
               return o3_new(cEx)("Invalid argument count. ( __setter__ )");
            *rval = pthis1->__setter__(argv[0].toInt32(),argv[1].toInt32());
            break;
         case 8:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( toHex )");
            *rval = pthis1->toHex();
            break;
         case 9:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( toBase64 )");
            *rval = pthis1->toBase64();
            break;
         case 10:
            if (argc < 1 && argc > 3)
               return o3_new(cEx)("Invalid argument count. ( write )");
            pthis1->write(argv[0].toStr(),argc > 1 ? argv[1].toInt32() : 0,argc > 2 ? argv[2].toStr() : "utf8");
            break;
         case 11:
            if (argc < 1 && argc > 4)
               return o3_new(cEx)("Invalid argument count. ( copy )");
            pthis1->copy(siBuf (argv[0].toScr()),argc > 1 ? argv[1].toInt32() : 0,argc > 2 ? argv[2].toInt32() : 0,argc > 3 ? argv[3].toInt32() : -1);
            break;
         case 12:
            if (argc != 2)
               return o3_new(cEx)("Invalid argument count. ( slice )");
            *rval = pthis1->slice(argv[0].toInt32(),argv[1].toInt32());
            break;
         case 13:
            if (argc < 1 && argc > 2)
               return o3_new(cEx)("Invalid argument count. ( find )");
            *rval = pthis1->find(argv[0].toStr(),argc > 1 ? argv[1].toInt32() : 0);
            break;
      }
      return ex;
}

siEx cScrBuf::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cScrBuf* pthis1 = (cScrBuf*) pthis;

      switch(index) {
         case 0:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( BufFromString )");
            *rval = o3_new(cScrBuf)(pthis1->BufFromString(argv[0].toStr()));
            break;
         case 1:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( BufFromHex )");
            *rval = o3_new(cScrBuf)(pthis1->BufFromHex(argv[0].toStr()));
            break;
         case 2:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( BufFromBase64 )");
            *rval = o3_new(cScrBuf)(pthis1->BufFromBase64(argv[0].toStr()));
            break;
         case 3:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( ScrBuf )");
            *rval = pthis1->ScrBuf(argv[0].toInt32());
            break;
      }
      return ex;
}

