namespace o3 {


Trait* cFastXmlDocument::select()
{
   return clsTraits();
}

Trait* cFastXmlDocument::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cFastXmlDocument",   0,                    0,              0,      cFastXmlNode::clsTraits()  },
         {      0,      Trait::TYPE_GET,        "cFastXmlDocument",   "documentElement",    clsInvoke,      0,      0                  },
         {      1,      Trait::TYPE_FUN,        "cFastXmlDocument",   "createElement",      clsInvoke,      1,      0                  },
         {      2,      Trait::TYPE_FUN,        "cFastXmlDocument",   "createTextNode",     clsInvoke,      2,      0                  },
         {      3,      Trait::TYPE_FUN,        "cFastXmlDocument",   "createComment",      clsInvoke,      3,      0                  },
         {      4,      Trait::TYPE_FUN,        "cFastXmlDocument",   "createCDATASection", clsInvoke,      4,      0                  },
         {      5,      Trait::TYPE_FUN,        "cFastXmlDocument",   "createAttribute",    clsInvoke,      5,      0                  },
         {      6,      Trait::TYPE_FUN,        "cFastXmlDocument",   "setProperty",        clsInvoke,      6,      0                  },
         {      7,      Trait::TYPE_FUN,        "cFastXmlDocument",   "importNode",         clsInvoke,      7,      0                  },
         {      8,      Trait::TYPE_FUN,        "cFastXmlDocument",   "getElementById",     clsInvoke,      8,      0                  },
         {      9,      Trait::TYPE_FUN,        "cFastXmlDocument",   "getElementsByTagName",clsInvoke,      9,      0                  },
         {      0,      Trait::TYPE_END,        "cFastXmlDocument",   0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cFastXmlDocument::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cFastXmlDocument",   0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_END,        "cFastXmlDocument",   0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cFastXmlDocument::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cFastXmlDocument* pthis1 = (cFastXmlDocument*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( documentElement )");
            *rval = siXmlElement(pthis1->documentElement());
            break;
         case 1:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( createElement )");
            *rval = siXmlElement(pthis1->createElement(argv[0].toStr()));
            break;
         case 2:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( createTextNode )");
            *rval = siXmlText(pthis1->createTextNode(argv[0].toStr()));
            break;
         case 3:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( createComment )");
            *rval = siXmlComment(pthis1->createComment(argv[0].toStr()));
            break;
         case 4:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( createCDATASection )");
            *rval = siXmlCDATASection(pthis1->createCDATASection(argv[0].toStr()));
            break;
         case 5:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( createAttribute )");
            *rval = siXmlAttr(pthis1->createAttribute(argv[0].toStr()));
            break;
         case 6:
            if (argc != 2)
               return o3_new(cEx)("Invalid argument count. ( setProperty )");
            *rval = pthis1->setProperty(argv[0].toStr(),argv[1].toStr());
            break;
         case 7:
            if (argc != 2)
               return o3_new(cEx)("Invalid argument count. ( importNode )");
            *rval = siXmlNode(pthis1->importNode(siXmlNode (argv[0].toScr()),argv[1].toBool()));
            break;
         case 8:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( getElementById )");
            *rval = siXmlNode(pthis1->getElementById(argv[0].toStr()));
            break;
         case 9:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( getElementsByTagName )");
            *rval = siXmlNodeList(pthis1->getElementsByTagName(argv[0].toStr()));
            break;
      }
      return ex;
}

siEx cFastXmlDocument::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      return ex;
}

}
