#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cFastXml::select()
{
   return clsTraits();
}

Trait* cFastXml::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cFastXml",           0,                    0,              0,      cScr::clsTraits()  },
         {      0,      Trait::TYPE_FUN,        "cFastXml",           "parseFromString",    clsInvoke,      0,      0                  },
         {      0,      Trait::TYPE_END,        "cFastXml",           0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cFastXml::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cFastXml",           0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_GET,        "cO3",                "fastxml",            extInvoke,      0,      0                  },
         {      0,      Trait::TYPE_END,        "cFastXml",           0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cFastXml::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cFastXml* pthis1 = (cFastXml*) pthis;

      switch(index) {
         case 0:
            if (argc < 1 && argc > 2)
               return o3_new(cEx)("Invalid argument count. ( parseFromString )");
            *rval = siXmlNode(pthis1->parseFromString(ctx,argv[0].toStr(),argc > 1 ? argv[1].toStr() : "text/xml"));
            break;
      }
      return ex;
}

siEx cFastXml::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cFastXml* pthis1 = (cFastXml*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( fastxml )");
            *rval = siXml(pthis1->fastxml(ctx));
            break;
      }
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
