#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cFs::select()
{
   return clsTraits();
}

Trait* cFs::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cFs",                0,                    0,              0,      cFsBase::clsTraits()  },
         {      0,      Trait::TYPE_END,        "cFs",                0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cFs::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cFs",                0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_GET,        "cO3",                "fs",                 extInvoke,      0,      0                  },
         {      1,      Trait::TYPE_GET,        "cO3",                "cwd",                extInvoke,      1,      0                  },
         {      2,      Trait::TYPE_GET,        "cO3",                "fsSafe",             extInvoke,      2,      0                  },
         {      3,      Trait::TYPE_FUN,        "cO3",                "openFileDialog",     extInvoke,      3,      0                  },
         {      4,      Trait::TYPE_FUN,        "cO3",                "saveFileDialog",     extInvoke,      4,      0                  },
         {      0,      Trait::TYPE_END,        "cFs",                0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cFs::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      return ex;
}

siEx cFs::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cFs* pthis1 = (cFs*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( fs )");
            *rval = pthis1->fs(ctx);
            break;
         case 1:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( cwd )");
            *rval = pthis1->cwd(ctx);
            break;
         case 2:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( fsSafe )");
            *rval = siFs(pthis1->fsSafe(ctx));
            break;
         case 3:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( openFileDialog )");
            *rval = siFs(pthis1->openFileDialog(ctx,argv[0].toStr()));
            break;
         case 4:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( saveFileDialog )");
            *rval = siFs(pthis1->saveFileDialog(ctx));
            break;
      }
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
