#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cJs::select()
{
   return clsTraits();
}

Trait* cJs::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cJs",                0,                    0,              0,      cScr::clsTraits()  },
         {      0,      Trait::TYPE_FUN,        "cJs",                "include",            clsInvoke,      0,      0                  },
         {      1,      Trait::TYPE_FUN,        "cJs",                "eval",               clsInvoke,      1,      0                  },
         {      0,      Trait::TYPE_END,        "cJs",                0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cJs::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cJs",                0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_GET,        "cO3",                "js",                 extInvoke,      0,      0                  },
         {      0,      Trait::TYPE_END,        "cJs",                0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cJs::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cJs* pthis1 = (cJs*) pthis;

      switch(index) {
         case 0:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( include )");
            *rval = pthis1->include(argv[0].toStr(),&ex);
            break;
         case 1:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( eval )");
            *rval = pthis1->eval(argv[0].toStr(),&ex);
            break;
      }
      return ex;
}

siEx cJs::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cJs* pthis1 = (cJs*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( js )");
            *rval = pthis1->js(ctx);
            break;
      }
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
