#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cKeyboardBase::select()
{
   return clsTraits();
}

Trait* cKeyboardBase::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cKeyboardBase",      0,                    0,              0,      cScr::clsTraits()  },
         {      0,      Trait::TYPE_FUN,        "cKeyboardBase",      "sendKeyDown",        clsInvoke,      0,      0                  },
         {      1,      Trait::TYPE_FUN,        "cKeyboardBase",      "sendKeyUp",          clsInvoke,      1,      0                  },
         {      2,      Trait::TYPE_FUN,        "cKeyboardBase",      "sendKeyPressed",     clsInvoke,      2,      0                  },
         {      0,      Trait::TYPE_END,        "cKeyboardBase",      0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cKeyboardBase::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cKeyboardBase",      0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_END,        "cKeyboardBase",      0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cKeyboardBase::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cKeyboardBase* pthis1 = (cKeyboardBase*) pthis;

      switch(index) {
         case 0:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( sendKeyDown )");
            pthis1->sendKeyDown(argv[0].toInt32());
            break;
         case 1:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( sendKeyUp )");
            pthis1->sendKeyUp(argv[0].toInt32());
            break;
         case 2:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( sendKeyPressed )");
            pthis1->sendKeyPressed(argv[0].toInt32());
            break;
      }
      return ex;
}

siEx cKeyboardBase::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
