#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cKeyboard::select()
{
   return clsTraits();
}

Trait* cKeyboard::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cKeyboard",          0,                    0,              0,      cKeyboardBase::clsTraits()  },
         {      0,      Trait::TYPE_END,        "cKeyboard",          0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cKeyboard::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cKeyboard",          0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_GET,        "cO3",                "keyboard",           extInvoke,      0,      0                  },
         {      0,      Trait::TYPE_END,        "cKeyboard",          0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cKeyboard::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      return ex;
}

siEx cKeyboard::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cKeyboard* pthis1 = (cKeyboard*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( keyboard )");
            *rval = pthis1->keyboard(ctx);
            break;
      }
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
