#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cMouse::select()
{
   return clsTraits();
}

Trait* cMouse::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cMouse",             0,                    0,              0,      cMouseBase::clsTraits()  },
         {      0,      Trait::TYPE_FUN,        "cMouse",             "sendMouseScrolled",  clsInvoke,      0,      0                  },
         {      0,      Trait::TYPE_END,        "cMouse",             0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cMouse::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cMouse",             0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_GET,        "cO3",                "mouse",              extInvoke,      0,      0                  },
         {      0,      Trait::TYPE_END,        "cMouse",             0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cMouse::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cMouse* pthis1 = (cMouse*) pthis;

      switch(index) {
         case 0:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( sendMouseScrolled )");
            pthis1->sendMouseScrolled(argv[0].toInt32());
            break;
      }
      return ex;
}

siEx cMouse::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cMouse* pthis1 = (cMouse*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( mouse )");
            *rval = pthis1->mouse(ctx);
            break;
      }
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
