#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cResource::select()
{
   return clsTraits();
}

Trait* cResource::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cResource",          0,                    0,              0,      cScr::clsTraits()  },
         {      0,      Trait::TYPE_FUN,        "cResource",          "unpack",             clsInvoke,      0,      0                  },
         {      1,      Trait::TYPE_FUN,        "cResource",          "list",               clsInvoke,      1,      0                  },
         {      2,      Trait::TYPE_FUN,        "cResource",          "get",                clsInvoke,      2,      0                  },
         {      2,      Trait::TYPE_FUN,        "cResource",          "get",                clsInvoke,      3,      0                  },
         {      3,      Trait::TYPE_FUN,        "cResource",          "protocolOpen",       clsInvoke,      4,      0                  },
         {      0,      Trait::TYPE_END,        "cResource",          0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cResource::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cResource",          0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_GET,        "cO3",                "resources",          extInvoke,      0,      0                  },
         {      0,      Trait::TYPE_END,        "cResource",          0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cResource::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cResource* pthis1 = (cResource*) pthis;

      switch(index) {
         case 0:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( unpack )");
            pthis1->unpack(ctx,siFs (argv[0].toScr()),&ex);
            break;
         case 1:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( list )");
            *rval = o3_new(tScrVec<Str>)(pthis1->list());
            break;
         case 2:
            if (argc==1) {
               *rval = o3_new(cScrBuf)(pthis1->get(argv[0].toStr()));
            }
            else if(argc==2) {
               *rval = pthis1->get(argv[0].toStr(),siFs (argv[1].toScr()));
            }
            else{
               return o3_new(cEx)("Invalid argument count.");
            }
            break;
         case 4:
            if (argc != 1)
               return o3_new(cEx)("Invalid argument count. ( protocolOpen )");
            *rval = siStream(pthis1->protocolOpen(argv[0].toStr()));
            break;
      }
      return ex;
}

siEx cResource::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cResource* pthis1 = (cResource*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( resources )");
            *rval = pthis1->resources(ctx);
            break;
      }
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
