#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cRSA::select()
{
   return clsTraits();
}

Trait* cRSA::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cRSA",               0,                    0,              0,      cScr::clsTraits()  },
         {      0,      Trait::TYPE_FUN,        "cRSA",               "encrypt",            clsInvoke,      0,      0                  },
         {      1,      Trait::TYPE_FUN,        "cRSA",               "decrypt",            clsInvoke,      1,      0                  },
         {      0,      Trait::TYPE_END,        "cRSA",               0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cRSA::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cRSA",               0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_GET,        "cO3",                "rsa",                extInvoke,      0,      0                  },
         {      0,      Trait::TYPE_END,        "cRSA",               0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cRSA::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cRSA* pthis1 = (cRSA*) pthis;

      switch(index) {
         case 0:
            if (argc < 3 && argc > 4)
               return o3_new(cEx)("Invalid argument count. ( encrypt )");
            *rval = o3_new(cScrBuf)(pthis1->encrypt(Buf(siBuf(argv[0].toScr())),Buf(siBuf(argv[1].toScr())),Buf(siBuf(argv[2].toScr())),argc > 3 ? argv[3].toBool() : true));
            break;
         case 1:
            if (argc < 3 && argc > 4)
               return o3_new(cEx)("Invalid argument count. ( decrypt )");
            *rval = o3_new(cScrBuf)(pthis1->decrypt(Buf(siBuf(argv[0].toScr())),Buf(siBuf(argv[1].toScr())),Buf(siBuf(argv[2].toScr())),argc > 3 ? argv[3].toBool() : true));
            break;
      }
      return ex;
}

siEx cRSA::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cRSA* pthis1 = (cRSA*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( rsa )");
            *rval = pthis1->rsa(ctx);
            break;
      }
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
