#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cScreenBase::select()
{
   return clsTraits();
}

Trait* cScreenBase::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cScreenBase",        0,                    0,              0,      cScr::clsTraits()  },
         {      0,      Trait::TYPE_GET,        "cScreenBase",        "width",              clsInvoke,      0,      0                  },
         {      1,      Trait::TYPE_GET,        "cScreenBase",        "height",             clsInvoke,      1,      0                  },
         {      0,      Trait::TYPE_END,        "cScreenBase",        0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cScreenBase::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cScreenBase",        0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_END,        "cScreenBase",        0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cScreenBase::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cScreenBase* pthis1 = (cScreenBase*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( width )");
            *rval = pthis1->width();
            break;
         case 1:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( height )");
            *rval = pthis1->height();
            break;
      }
      return ex;
}

siEx cScreenBase::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
