#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cScreen::select()
{
   return clsTraits();
}

Trait* cScreen::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cScreen",            0,                    0,              0,      cScreenBase::clsTraits()  },
         {      0,      Trait::TYPE_GET,        "cScreen",            "width",              clsInvoke,      0,      0                  },
         {      1,      Trait::TYPE_GET,        "cScreen",            "height",             clsInvoke,      1,      0                  },
         {      0,      Trait::TYPE_END,        "cScreen",            0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cScreen::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cScreen",            0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_GET,        "cO3",                "screen",             extInvoke,      0,      0                  },
         {      0,      Trait::TYPE_END,        "cScreen",            0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cScreen::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cScreen* pthis1 = (cScreen*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( width )");
            *rval = pthis1->width();
            break;
         case 1:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( height )");
            *rval = pthis1->height();
            break;
      }
      return ex;
}

siEx cScreen::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cScreen* pthis1 = (cScreen*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( screen )");
            *rval = siScr(pthis1->screen(ctx));
            break;
      }
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
