#ifdef O3_WITH_GLUE
#pragma warning( disable : 4100)
#pragma warning( disable : 4189)
namespace o3 {


Trait* cWindow::select()
{
   return clsTraits();
}

Trait* cWindow::clsTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cWindow",            0,                    0,              0,      cWindowBase::clsTraits()  },
         {      0,      Trait::TYPE_END,        "cWindow",            0,                    0,              0,      0                  },
      };

      return TRAITS;
}

Trait* cWindow::extTraits()
{
      static Trait TRAITS[] = {
         {      0,      Trait::TYPE_BEGIN,      "cWindow",            0,                    0,              0,      0                  },
         {      0,      Trait::TYPE_GET,        "cO3",                "window",             extInvoke,      0,      0                  },
         {      0,      Trait::TYPE_END,        "cWindow",            0,                    0,              0,      0                  },
      };

      return TRAITS;
}

siEx cWindow::clsInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      return ex;
}

siEx cWindow::extInvoke(iScr* pthis, iCtx* ctx, int index, int argc,
           const Var* argv, Var* rval)
{
      siEx ex;
      cWindow* pthis1 = (cWindow*) pthis;

      switch(index) {
         case 0:
            if (argc != 0)
               return o3_new(cEx)("Invalid argument count. ( window )");
            *rval = pthis1->window(ctx);
            break;
      }
      return ex;
}

}
#endif
#pragma warning(default : 4100)
#pragma warning(default : 4189)
